const { compId = '', extInfo = {} } = request.body
const { pageNum = 1 } = extInfo;
let { contextType = '' } = session;
const caseMap = {
    ERR: 'BA04450C-159C-44DB-A33C-BAAF901A3A7F', // 通用异常
    MRCH_POINT_MALL: '4DEDFE8C-EB3D-4D84-AFDB-336FCD471C25', // 商家等级
    EXCHANGE_EMPTY: '15DF44CF-070D-45F4-BE30-4EE780BE6C52', // 兑换记录空数据
    EXCHANGE_REQ_1: 'BA449600-CD9F-437E-B112-427CB7C5E5BB', // 兑换记录第一页
    EXCHANGE_REQ_2: '447891E8-1716-4E5A-B426-E1E9D19B5C75', // 兑换记录第二页
    MRCH_POINT_EXCHANGE_ORDER_DETAIL: 'ACB0BB07-7EF2-4B59-ABF7-8CEFD1826B64', // 兑换记录详情
    PROFILE_CENTER: 'B2386322-ED4E-4716-8F3D-00FD8D4044C5' // 个人中心
}

if( !contextType ) return caseMap.ERR

if(compId === 'MRCH_POINT_EXCHANGE_ORDER_LIST') {
    if( contextType === 'EXCHANGE_EMPTY' ) return caseMap.EXCHANGE_EMPTY
    if(contextType === 'EXCHANGE_LIST_DATA') {
        return caseMap[`EXCHANGE_REQ_${pageNum}`] || caseMap.ERR
    }
    return caseMap.ERR
}

return caseMap[compId] || caseMap.ERR
